mapper.select:
select , c.spec as "spec", c.name as "productName", c.number as "number", c.version as "version", e.name as "unitName", b.name as "testItemName", a.count as "count" from ex_inspection_inf d left join pd_use_package_detail a on a.product_Id=d.product_id left join pd_use_package b on b.id=a.package_id and d.code=b.code and b.del_flag='0' left join pd_product c on c.id=a.product_id and c.del_flag='0' left join pd_unit e on e.id=c.spec_unit_id and e.del_flag='0' where a.del_flag='0'
AND b.code=#{entity.code}
AND d.jy_id=#{entity.jyId}
AND d.status=#{entity.status}
AND c.number LIKE concat('%',#{entity.number},'%')
AND ( c.name LIKE concat('%',#{entity.productName},'%') or c.py LIKE concat('%',#{entity.productName},'%') or c.wb LIKE concat('%',#{entity.productName},'%') or c.zdy LIKE concat('%',#{entity.productName},'%') or c.bname LIKE concat('%',#{entity.productName},'%') or c.bpy LIKE concat('%',#{entity.productName},'%') or c.bwb LIKE concat('%',#{entity.productName},'%') )

SELECT a.*, d.number as "number", d.name as "productName", d.spec as spec, d.id as "productId", d.version as version, d.spec_quantity as specQuantity, d.product_flag as "productFlag", c.status as "status" FROM pd_use_package_detail a left join pd_use_package b on a.package_id=b.id left join ex_inspection_inf c on b.code=c.code and c.product_id=a.product_id
AND c.jy_id=#{jyId}
left join pd_product d on d.id=a.product_id where a.del_flag='0'
AND b.code=#{code}
GROUP BY productId
