mapper.select:
SELECT
,

FROM ex_his_depart_inf a

where 1=1
AND	a.del_flag =#{DEL_FLAG_NORMAL}

	AND a.fsf_ksbh = #{fsfKsbh}


	AND a.fsf_ksmc LIKE concat('%',#{fsfKsmc},'%')


	AND a.spd_depart_id = #{spdDepartId}

SELECT
FROM ex_his_depart_inf a where 1=1 AND a.del_flag ="0"
AND a.fsf_ksbh = #{fsfKsbh}
