mapper.select:
SELECT , , ,
FROM pd_allocation_detail a
WHERE 1=1
AND a.allocation_no = #{allocationNo}
AND a.product_attr = #{productAttr}
ORDER BY a.create_time DESC

mapper.update:
UPDATE
	pd_allocation_detail
SET
	arrival_num = IFNULL(arrival_num,0) + #{arrivalNum}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and allocation_no = #{allocationNo}
	and product_id = #{productId}

UPDATE
	pd_allocation_detail
SET
	arrival_num = IFNULL(arrival_num,0) - #{arrivalNum}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and allocation_no = #{allocationNo}
	and product_id = #{productId}

mapper.select:
select a.id as "packageId", a.code as "packageCode", a.name as "packageName", a.sum as "packageNum", a.remarks as "remarks", sum(b.allocation_num) / a.sum as "allocation_num" from PD_PACKAGE a LEFT JOIN pd_allocation_detail b on b.package_id=a.id where 1=1
AND b.allocation_no = #{allocationNo}
AND b.product_attr = #{productAttr}
GROUP BY PACKAGE_ID
