mapper.select:
SELECT
,
,

FROM pd_allocation_record a




    a.del_flag =#{DEL_FLAG_NORMAL}

        AND a.id = #{id}


        AND a.allocation_no = #{allocationNo}


        AND a.audit_status = #{auditStatus}


        AND a.submit_status = #{submitStatus}


        AND a.in_dept_id LIKE concat('%',#{inDeptId},'%')


        AND d.depart_name LIKE concat('%',#{inDeptName},'%')


        AND a.out_dept_id LIKE concat('%',#{outDeptId},'%')


        AND a.depart_parent_id = #{departParentId}


        and a.submit_status IN

            #{item}



        and a.audit_status IN

            #{item}



        AND a.allocation_date >= #{queryDateStart}


        AND a.allocation_date <= #{queryDateEnd}


        and a.in_dept_id IN

            #{item}



        and a.out_dept_id IN

            #{item}


    ORDER BY  field(a.audit_status, 1, 3, 2),a.update_time ASC

SELECT DISTINCT a.id as id, a.allocation_no as allocationNo, a.allocation_date as allocationDate, IFNULL(a.arrival_count,0) as arrivalCount, a.total_num as totalNum, f.depart_name AS inDeptName, e.depart_name AS outDeptName, a.audit_status as auditStatus, c.realname as auditByName FROM pd_allocation_record a LEFT JOIN pd_allocation_detail b on a.allocation_no = b.allocation_no LEFT JOIN sys_user c ON c.id = a.audit_by LEFT JOIN pd_product d on b.product_id = d.id LEFT JOIN sys_depart e ON a.out_dept_id = e.id LEFT JOIN sys_depart f ON a.in_dept_id = f.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.audit_status = '2'
AND a.id = #{id}
AND a.allocation_no LIKE concat('%',#{allocationNo},'%')
AND ( d.name LIKE concat('%',#{productName},'%') or d.py LIKE concat('%',#{productName},'%') or d.wb LIKE concat('%',#{productName},'%') or d.bname LIKE concat('%',#{productName},'%') or d.bpy LIKE concat('%',#{productName},'%') or d.bwb LIKE concat('%',#{productName},'%') or d.zdy LIKE concat('%',#{productName},'%') )
AND d.number LIKE concat('%',#{number},'%')
AND a.allocation_date >= #{queryDateStart}
AND a.allocation_date <= #{queryDateEnd}
AND a.in_dept_id = #{inDeptId}
AND a.out_dept_id = #{outDeptId}
order by a.allocation_date desc

UPDATE
	pd_allocation_record
SET
	arrival_count = IFNULL(arrival_count,0) + #{arrivalCount}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and allocation_no = #{allocationNo}

UPDATE
	pd_allocation_record
SET
	arrival_count = IFNULL(arrival_count,0) + #{arrivalCount}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and allocation_no = #{allocationNo}
