mapper.select:
SELECT , , ,
FROM pd_apply_detail a
WHERE 1=1
AND a.apply_no = #{applyNo}
AND a.product_attr = #{productAttr}
ORDER BY a.create_time DESC

mapper.update:
UPDATE
	pd_apply_detail
SET
	arrival_num = IFNULL(arrival_num,0) + #{arrivalNum}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and apply_no = #{applyNo}
	and product_id = #{productId}

UPDATE
	pd_apply_detail
SET
	arrival_num = IFNULL(arrival_num,0) - #{arrivalNum}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and apply_no = #{applyNo}
	and product_id = #{productId}

mapper.select:
select a.id as "packageId", a.code as "packageCode", a.name as "packageName", a.sum as "packageNum", a.remarks as "remarks", sum(b.apply_num) / a.sum as "applyNum" from PD_PACKAGE a LEFT JOIN pd_apply_detail b on b.package_id=a.id where 1=1
AND b.apply_no = #{applyNo}
AND b.product_attr = #{productAttr}
GROUP BY PACKAGE_ID
