mapper.select:
SELECT , , ,
FROM pd_apply_detail a
WHERE 1=1
AND a.apply_no = #{applyNo}
AND a.product_attr = #{productAttr}
ORDER BY a.create_time DESC

mapper.update:
UPDATE
	pd_apply_detail
SET
	arrival_num = IFNULL(arrival_num,0) + #{arrivalNum}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and apply_no = #{applyNo}
	and product_id = #{productId}

UPDATE
	pd_apply_detail
SET
	arrival_num = IFNULL(arrival_num,0) - #{arrivalNum}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and apply_no = #{applyNo}
	and product_id = #{productId}

mapper.select:
select a.package_id as "packageId", a.id as "packageRecordId", a.remarks as "remarks", a.package_bar_code as "packageBarCode", a.create_by as "createBy", a.create_time as "createTime", "1" as "applyNum", c.package_code as "packageCode", c.package_name as "packageName", c.package_sum as "packageSum" from pd_package_record a left join pd_package c on c.id=a.package_id where a.id in(select b.package_record_id from pd_apply_detail b where 1=1
AND b.apply_no = #{applyNo}
AND b.product_attr = #{productAttr}
AND b.package_record_id = #{packageRecordId}
GROUP BY b.package_record_id )
