mapper.select:
SELECT
,
,

FROM pd_apply_order a




    a.del_flag =#{DEL_FLAG_NORMAL}

        AND a.id = #{id}


        AND a.apply_no = #{applyNo}


        AND a.audit_status = #{auditStatus}


        AND a.submit_status = #{submitStatus}


        AND f.depart_name = #{deptName}


        AND a.out_depart_id = #{outDepartId}


        AND a.depart_id = #{departId}


        AND a.depart_parent_id = #{departParentId}


        and a.submit_status IN

            #{item}



        and a.audit_status IN

            #{item}



        AND a.apply_date >= #{queryDateStart}


        AND a.apply_date <= #{queryDateEnd}


        and a.depart_id IN

            #{item}


    ORDER BY  field(a.audit_status, 1, 3, 2),a.update_time ASC

SELECT DISTINCT a.id as id, a.apply_no as applyNo, a.apply_date as applyDate, a.total_num as totalNum, IFNULL(a.arrival_count,0) as arrivalCount, f.depart_name AS deptName, a.audit_status as auditStatus, c.realname as auditByName FROM pd_apply_order a LEFT JOIN pd_apply_detail b on a.apply_no = b.apply_no LEFT JOIN sys_user c ON c.id = a.audit_by LEFT JOIN pd_product d on b.product_id = d.id LEFT JOIN sys_depart f ON a.depart_id = f.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.audit_status = '2'
AND a.id = #{id}
AND a.apply_no LIKE concat('%',#{applyNo},'%')
AND ( d.name LIKE concat('%',#{productName},'%') or d.py LIKE concat('%',#{productName},'%') or d.wb LIKE concat('%',#{productName},'%') or d.bname LIKE concat('%',#{productName},'%') or d.bpy LIKE concat('%',#{productName},'%') or d.bwb LIKE concat('%',#{productName},'%') or d.zdy LIKE concat('%',#{productName},'%') )
AND d.number LIKE concat('%',#{number},'%')
AND a.apply_date >= #{queryDateStart}
AND a.apply_date <= #{queryDateEnd}
AND a.depart_id = #{departId}
AND a.depart_parent_id = #{departParentId}
order by a.apply_date desc

UPDATE
	pd_apply_order
SET
	arrival_count = IFNULL(arrival_count,0) + #{arrivalCount}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and apply_no = #{applyNo}

UPDATE
	pd_apply_order
SET
	arrival_count = IFNULL(arrival_count,0) + #{arrivalCount}
WHERE
	del_flag =#{DEL_FLAG_NORMAL}
	and apply_no = #{applyNo}

SELECT
sum(a.total_num) AS "applyCount",
sum(b.total_num)  AS "dayApplyNum"
from pd_apply_order a
LEFT JOIN  pd_apply_order b ON  a.id=b.id
AND  DATE_FORMAT(b.apply_date,'%Y-%m-%d')=DATE_FORMAT(#{applyDate},'%Y-%m-%d')
WHERE  a.del_flag=#{DEL_FLAG_NORMAL}
AND  a.audit_status='2'

    AND a.depart_id = #{departId}


    AND a.depart_parent_id = #{departParentId}


    and a.depart_id IN

        #{item}

SELECT date(c.dday) as x, sum(c.y) as y FROM ( SELECT datelist as dday ,"0" as y FROM calendar where DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(datelist) and date(datelist) <=CURDATE() UNION ALL SELECT a.apply_date as dday, a.total_num as y FROM pd_apply_order a where a.del_flag=#{DEL_FLAG_NORMAL} AND a.audit_status='2'
AND a.depart_id = #{departId}
AND a.depart_parent_id = #{departParentId}
and a.depart_id IN
#{item}
AND DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(a.apply_date) AND date(a.apply_date) <=CURDATE() ) c GROUP BY x
