mapper.select:
SELECT
,
,

FROM pd_apply_order a




    a.del_flag =#{DEL_FLAG_NORMAL}

        AND a.id = #{id}


        AND a.apply_no = #{applyNo}


        AND a.audit_status = #{auditStatus}


        AND a.submit_status = #{submitStatus}


        AND f.depart_name = #{deptName}


        AND a.depart_id = #{departId}


        and a.submit_status IN

            #{item}


    ORDER BY   audit_status , update_time DESC

SELECT DISTINCT a.id as id, a.apply_no as applyNo, a.apply_date as applyDate, a.total_num as totalNum, f.depart_name AS deptName, a.audit_status as auditStatus, c.realname as auditByName FROM pd_apply_order a LEFT JOIN pd_apply_detail b on a.apply_no = b.apply_no LEFT JOIN sys_user c ON c.id = a.audit_by LEFT JOIN pd_product d on b.product_id = d.id LEFT JOIN sys_depart f ON a.depart_id = f.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.audit_status = '2'
AND a.id = #{id}
AND a.apply_no LIKE concat('%',#{applyNo},'%')
AND ( d.name LIKE concat('%',#{productName},'%') or d.py LIKE concat('%',#{productName},'%') or d.wb LIKE concat('%',#{productName},'%') or d.bname LIKE concat('%',#{productName},'%') or d.bpy LIKE concat('%',#{productName},'%') or d.bwb LIKE concat('%',#{productName},'%') or d.zdy LIKE concat('%',#{productName},'%') )
AND d.number LIKE concat('%',#{number},'%')
AND a.apply_date >= #{queryDateStart}
AND a.apply_date <= #{queryDateEnd}
order by a.apply_date desc
