mapper.update:
UPDATE pd_category
SET
del_flag = #{DEL_FLAG_DELETE},
create_time =#{createTime},
create_by =#{createBy}
  where id = #{id}

mapper.select:
SELECT
*
FROM pd_category a

	a.del_flag = #{DEL_FLAG_NORMAL}


	AND a.type =#{type}


	AND a.parent_id =#{parentId}


	AND a.name LIKE concat('%',#{name},'%')
	or a.py LIKE concat('%',#{name},'%')
	or a.wb LIKE concat('%',#{name},'%')
	or a.zdy LIKE concat('%',#{name},'%')
