mapper.select:
SELECT
,
b.realname AS dosageByName
FROM
`pd_dosage` a
LEFT JOIN sys_user b ON a.dosage_by = b.id

    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.dosage_no = #{dosageNo}


        AND a.in_hospital_no = #{inHospitalNo}


        AND a.dosage_by = #{dosageBy}


        AND a.depart_id = #{departId}


        AND a.depart_parent_id = #{departParentId}


ORDER BY a.update_time desc,a.create_time desc

SELECT
,
,
b.realname AS dosageByName
FROM  `pd_dosage` a

LEFT JOIN sys_user b ON a.dosage_by = b.id

    a.del_flag = #{entity.DEL_FLAG_NORMAL}

        AND a.dosage_no = #{entity.dosageNo}


        AND (a.in_hospital_no LIKE concat('%', #{entity.inHospitalNo},'%') or a.outpatient_number LIKE concat('%',#{entity.inHospitalNo},'%'))


        AND a.dosage_by = #{entity.dosageBy}


        AND a.depart_id = #{entity.departId}


        and a.depart_id IN

            #{item}



        AND a.depart_parent_id = #{entity.departParentId}


ORDER BY a.update_time desc,a.create_time desc

SELECT
sum(a.total_sum) AS "orderCount",
sum(b.total_sum) AS "dayOrderNum"
FROM
pd_dosage a
LEFT JOIN pd_dosage b ON a.id = b.id
AND DATE_FORMAT(b.dosage_date,'%Y-%m-%d') = DATE_FORMAT(#{dosageDate}, '%Y-%m-%d')

a.del_flag = #{DEL_FLAG_NORMAL}

    AND a.depart_id = #{departId}


    AND a.depart_parent_id = #{departParentId}


    and a.depart_id IN

        #{item}



        AND a.dosage_date >= #{queryDateStart}


        AND a.dosage_date <= #{queryDateEnd}

SELECT date(c.dday) as x, sum(c.y) as y FROM ( SELECT datelist as dday ,"0" as y FROM calendar where DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(datelist) and date(datelist)<=CURDATE() UNION ALL SELECT a.dosage_date AS dday, a.total_sum AS y FROM pd_dosage a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.depart_id = #{departId}
AND a.depart_parent_id = #{departParentId}
and a.depart_id IN
#{item}
AND DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(a.dosage_date) and date(a.dosage_date) <=CURDATE() ) c GROUP BY x

select IFNULL(d.name,'其他') as x, IFNULL(sum(a.total_price),0) as y, sum(b.dosage_count) as z from pd_dosage a left join pd_dosage_detail b on a.id=b.dosage_id left join pd_product c on b.product_id=c.id left join pd_category d on d.id=c.category_two where a.del_flag='0'
and a.depart_id IN
#{item}
AND a.depart_parent_id = #{departParentId}
AND a.dosage_date >= #{queryDateStart}
AND a.dosage_date <= #{queryDateEnd}
group by x order by y desc

SELECT
,
su.realname AS dosageByName,
,
,
,
,
,
,

FROM pd_dosage a
LEFT JOIN sys_user su ON a.dosage_by = su.id








    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.dosage_no = #{dosageNo}


        AND a.depart_id = #{departId}


        AND a.depart_parent_id = #{departParentId}


        AND a.depart_id IN

            #{item}



        AND a.in_hospital_no = #{inHospitalNo}


        AND a.patient_info LIKE concat('%',#{patientInfo},'%')


        AND (pp.name LIKE concat('%',#{productName},'%')
        or pp.py LIKE concat('%',#{productName},'%')
        or pp.wb LIKE concat('%',#{productName},'%')
        or pp.zdy LIKE concat('%',#{productName},'%')
        or pp.bname LIKE concat('%',#{productName},'%')
        or pp.bpy LIKE concat('%',#{productName},'%')
        or pp.bwb LIKE concat('%',#{productName},'%')
        )


        AND pp.version = #{version}


        AND pp.spec = #{spec}


        AND pp.category_one = #{categoryOne}


        AND pp.number LIKE concat('%',#{productNumber},'%')


        AND dd.batch_no = #{batchNo}


        AND dd.hy_charged IN

            #{item}



        AND dd.hy_charged = #{hyCharged}


        AND dd.ref_bar_code = #{refBarCode}


        AND e.supplier_id = #{supplierId}


        AND date_format(a.dosage_date, "%Y-%m-%d") >= #{queryDateStart}


        AND date_format(a.dosage_date, "%Y-%m-%d") <= #{queryDateEnd}

    order by a.create_time desc

