mapper.select:
SELECT b.remarks AS remarks, a.*, b.invoice_reg_no AS invoice_reg_no, b.invoice_no AS invoice_no, b.invoice_code AS invoice_code, b.invoice_data AS invoice_data, b.invoice_money as invoice_money, c.batch_no AS batch_no, c.exp_date AS exp_date, e.stock_num AS stock_num, f.number AS product_number, f.NAME AS product_name, f.spec AS spec, g.id AS supplier_id, g.NAME AS supplier_name, h.id AS vender_id, h.NAME AS vender_name, i.id AS unit_id, i.NAME AS unit_name FROM pd_invoice_detail a LEFT JOIN pd_invoice b ON a.invoice_id = b.id LEFT JOIN pd_stock_record_detail c ON a.bill_detail_id = c.id LEFT JOIN pd_stock_record d ON d.id = c.record_id LEFT JOIN pd_product_stock e ON c.id = e.record_detail_id LEFT JOIN pd_product f ON a.product_id = f.id LEFT JOIN pd_supplier g ON c.supplier_id = g.id LEFT JOIN pd_vender h ON f.vender_id = h.id LEFT JOIN pd_unit i ON f.unit_id = i.id
a.del_flag = #{entity.DEL_FLAG_NORMAL}
AND a.depart_id = #{entity.departId}
AND a.depart_parent_id = #{entity.departParentId}
AND a.status = #{entity.status}
AND a.bill_no LIKE concat('%',#{entity.billNo},'%')
AND b.invoice_reg_no LIKE concat('%',#{entity.invoiceRegNo},'%')
AND b.invoice_no LIKE concat('%',#{entity.invoiceNo},'%')
AND b.invoice_code LIKE concat('%',#{entity.invoiceCode},'%')
AND date_format(b.invoice_data,'%Y-%m-%d') >= #{entity.queryInvoiceDateStart}
AND date_format(b.invoice_data,'%Y-%m-%d') <= #{entity.queryInvoiceDateEnd}
AND c.exp_date >= #{entity.queryExpDateStart}
AND c.exp_date <= #{entity.queryExpDateEnd}
AND date_format(d.audit_date,'%Y-%m-%d') >= #{entity.queryDateStart}
AND date_format(d.audit_date,'%Y-%m-%d') <= #{entity.queryDateEnd}
AND d.supplier_id = #{entity.supplierId}
AND f.number LIKE concat('%',#{entity.productNumber},'%')
AND f.vender_id = #{entity.venderId}
AND f.spec LIKE concat('%',#{entity.spec},'%')
AND f.version LIKE concat('%',#{entity.version},'%')
AND f.registration LIKE concat('%',#{entity.registration},'%')
AND ( f.number LIKE concat('%',#{entity.productName},'%') or f.name LIKE concat('%',#{entity.productName},'%') or f.py LIKE concat('%',#{entity.productName},'%') or f.wb LIKE concat('%',#{entity.productName},'%') or f.bname LIKE concat('%',#{entity.productName},'%') or f.bpy LIKE concat('%',#{entity.productName},'%') or f.bwb LIKE concat('%',#{entity.productName},'%') or f.zdy LIKE concat('%',#{entity.productName},'%') )
order by b.create_time desc,b.invoice_reg_no desc

SELECT a.id AS bill_detail_id, a.product_id AS product_id, a.product_num AS num, a.purchase_price AS price, a.product_num * IFNULL( a.purchase_price, 0 ) AS money, a.batch_no AS batch_no, a.exp_date AS exp_date, b.record_no AS bill_no, b.id AS bill_id, b.audit_date AS bill_date, c.id AS product_stock_id, c.stock_num AS stock_num, d.id AS id, d.invoice_id AS invoice_id, d.status AS status, e.remarks AS remarks, e.invoice_reg_no AS invoice_reg_no, e.invoice_no AS invoice_no, e.invoice_code AS invoice_code, e.invoice_data AS invoice_data, e.invoice_money as invoice_money, f.number AS product_number, f.NAME AS product_name, f.spec AS spec, g.id AS supplier_id, g.NAME AS supplier_name, h.id as vender_id, h.name AS vender_name, i.id as unit_id, i.name AS unit_name FROM pd_stock_record_detail a LEFT JOIN pd_stock_record b ON b.id = a.record_id LEFT JOIN pd_product_stock c ON a.id = c.record_detail_id LEFT JOIN (select * from pd_invoice_detail where del_flag = '0') d ON d.bill_detail_id = a.id LEFT JOIN (select * from pd_invoice where del_flag = '0') e ON d.invoice_id = e.id LEFT JOIN pd_product f ON a.product_id = f.id LEFT JOIN pd_supplier g ON a.supplier_id = g.id left join pd_vender h on f.vender_id = h.id LEFT JOIN pd_unit i ON f.unit_id = i.id
a.del_flag = #{entity.DEL_FLAG_NORMAL} and b.record_type = '1' and b.supplier_id is not null and b.supplier_id <> '' and b.audit_status = '2'
AND a.depart_id = #{entity.departId}
AND a.depart_parent_id = #{entity.departParentId}
AND a.exp_date >= #{entity.queryExpDateStart}
AND a.exp_date <= #{entity.queryExpDateEnd}
AND b.audit_status = #{entity.auditStatus}
and a.id IN
#{billDetailId}
AND b.record_no LIKE concat('%',#{entity.recordNo},'%')
AND date_format(b.audit_date,'%Y-%m-%d') >= #{entity.queryDateStart}
AND date_format(b.audit_date,'%Y-%m-%d') <= #{entity.queryDateEnd}
AND b.supplier_id = #{entity.supplierId}
AND d.status = #{entity.status}
AND f.number LIKE concat('%',#{entity.productNumber},'%')
AND f.vender_id = #{entity.venderId}
AND f.spec LIKE concat('%',#{entity.spec},'%')
AND f.version LIKE concat('%',#{entity.version},'%')
AND f.registration LIKE concat('%',#{entity.registration},'%')
AND ( f.number LIKE concat('%',#{entity.productName},'%') or f.name LIKE concat('%',#{entity.productName},'%') or f.py LIKE concat('%',#{entity.productName},'%') or f.wb LIKE concat('%',#{entity.productName},'%') or f.bname LIKE concat('%',#{entity.productName},'%') or f.bpy LIKE concat('%',#{entity.productName},'%') or f.bwb LIKE concat('%',#{entity.productName},'%') or f.zdy LIKE concat('%',#{entity.productName},'%') )
ORDER BY a.create_time DESC,a.product_id DESC,b.record_no DESC
