mapper.select:
SELECT a.*, c.code, c.name, c.sum FROM pd_package_record a LEFT JOIN pd_package c ON a.package_id = c.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND ( c.name LIKE concat('%',#{name},'%') or c.py LIKE concat('%',#{name},'%') or c.wb LIKE concat('%',#{name},'%') or c.zdy LIKE concat('%',#{name},'%') )
AND c.code LIKE concat('%',#{code},'%')
AND a.package_bar_code = #{packageBarCode}
AND a.status = #{status}
AND a.depart_parent_id = #{departParentId}
AND a.depart_id = #{departId}
AND a.id IN
#{item}
AND a.id in ( SELECT DISTINCT m.record_id FROM pd_package_record_detail m LEFT JOIN pd_product n ON m.product_id = n.id WHERE n.number LIKE concat('%',#{productName},'%') or n.name LIKE concat('%',#{productName},'%') or n.py LIKE concat('%',#{productName},'%') or n.wb LIKE concat('%',#{productName},'%') or n.bname LIKE concat('%',#{productName},'%') or n.bpy LIKE concat('%',#{productName},'%') or n.bwb LIKE concat('%',#{productName},'%') or n.zdy LIKE concat('%',#{productName},'%') )
AND a.id in ( SELECT DISTINCT m.record_id FROM pd_package_record_detail m LEFT JOIN pd_product n ON m.product_id = n.id WHERE n.number LIKE concat('%',#{productNumber},'%') )
ORDER BY a.update_time desc
