mapper.select:
SELECT DISTINCT , , , , ,
FROM pd_product_stock a
LEFT JOIN pd_dosage_detail p ON a.product_id = p.product_id AND a.product_bar_code = p.product_bar_code AND a.batch_no = p.batch_no LEFT JOIN pd_dosage s ON p.dosage_id = s.id
1 = 1
AND a.depart_id = #{departId}
AND a.depart_parent_id = #{departParentId}
AND c.number LIKE concat('%',#{number},'%')
AND a.batch_no = #{batchNo}
AND a.nestat_status = #{nestatStatus}
AND c.version = #{version}
AND c.spec = #{spec}
AND c.name LIKE CONCAT('%', #{productName}, '%')
AND c.registration LIKE concat('%',#{registration},'%')
AND s.in_hospital_no LIKE CONCAT('%', #{inHospitalNo}, '%')
group by a.product_id,a.product_bar_code,a.batch_no,a.exp_date

		SELECT
        sum(a.stock_num) AS "stockCount"
        from pd_product_stock a
        WHERE  a.del_flag=#{DEL_FLAG_NORMAL}

            AND a.depart_id = #{departId}


            AND a.depart_parent_id = #{departParentId}


			AND a.depart_id IN

				#{item}

SELECT date(c.dday) as x, sum(c.y) as y FROM ( SELECT datelist as dday ,"0" as y FROM calendar where DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(datelist) and date(datelist) <=CURDATE() UNION ALL SELECT a.create_time as dday, a.stock_num as y FROM pd_product_stock a where 1=1
AND a.depart_id = #{departId}
AND a.depart_parent_id = #{departParentId}
AND a.depart_id IN
#{item}
AND DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(a.create_time) and date(a.create_time) <=CURDATE() ) c GROUP BY x

SELECT
,
