mapper.select:
SELECT
,
,

FROM pd_purchase_order a




    a.del_flag =#{DEL_FLAG_NORMAL}

        AND a.id = #{id}


        AND a.order_no = #{orderNo}


        AND a.audit_status = #{auditStatus}


        AND a.submit_status = #{submitStatus}


        AND f.depart_name = #{deptName}


        AND a.depart_id = #{departId}


        AND a.depart_parent_id = #{departParentId}


        AND a.merge_order_no = #{mergeOrderNo}


        and a.submit_status IN

            #{item}




        and a.audit_status IN

            #{item}



        and a.depart_id IN

            #{item}



        AND a.order_date >= #{queryDateStart}


        AND a.order_date <= #{queryDateEnd}

    ORDER BY  field(a.audit_status, 1, 3, 2),a.update_time ASC

SELECT DISTINCT a.id as purchaseId, a.merge_order_no as orderNo, a.purchase_date as orderDate, f.depart_name AS deptName, a.audit_status as auditStatus, c.realname as auditByName FROM pd_purchase_order_merge a LEFT JOIN pd_purchase_order g on g.merge_order_no = a.merge_order_no LEFT JOIN pd_purchase_detail b on b.order_no = g.order_no LEFT JOIN sys_user c ON c.id = a.merge_by LEFT JOIN pd_product d on b.product_id = d.id LEFT JOIN sys_depart f ON a.depart_id = f.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.audit_status = '2' AND g.audit_status='2'
AND a.id = #{id}
AND a.merge_order_no LIKE concat('%',#{mergeOrderNo},'%')
AND ( d.name LIKE concat('%',#{productName},'%') or d.py LIKE concat('%',#{productName},'%') or d.wb LIKE concat('%',#{productName},'%') or d.bname LIKE concat('%',#{productName},'%') or d.bpy LIKE concat('%',#{productName},'%') or d.bwb LIKE concat('%',#{productName},'%') or d.zdy LIKE concat('%',#{productName},'%') )
AND d.supplier_id = #{supplierId}
AND d.number LIKE concat('%',#{productNumber},'%')
AND a.purchase_date >= #{queryDateStart}
AND a.purchase_date <= #{queryDateEnd}
AND a.depart_parent_id = #{departParentId}
order by a.purchase_date desc

mapper.update:
UPDATE pd_purchase_order
SET merge_order_no = #{mergeOrderNo},
    update_by = #{updateBy},
    update_time = #{updateTime}
WHERE order_no IN ${orderNos}

mapper.select:
 SELECT
 sum(a.total_num) AS "orderCount",
 sum(b.total_num)  AS "dayOrderNum"
 from pd_purchase_order a
LEFT JOIN  pd_purchase_order b ON  a.id=b.id
  AND DATE_FORMAT(b.order_date,'%Y-%m-%d')=DATE_FORMAT(#{orderDate},'%Y-%m-%d')
  WHERE  a.del_flag=#{DEL_FLAG_NORMAL}
AND  a.audit_status='2'

      AND a.depart_id = #{departId}


      AND a.depart_parent_id = #{departParentId}


      and a.depart_id IN

          #{item}

SELECT date(c.dday) as x, sum(c.y) as y FROM ( SELECT datelist as dday ,"0" as y FROM calendar where DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(datelist) and date(datelist)<=CURDATE() UNION ALL SELECT a.order_date as dday, a.total_num as y FROM pd_purchase_order a where a.del_flag=#{DEL_FLAG_NORMAL} AND a.audit_status='2'
AND a.depart_id = #{departId}
AND a.depart_parent_id = #{departParentId}
and a.depart_id IN
#{item}
AND DATE_SUB(CURDATE(), INTERVAL 6 DAY) <= date(a.order_date) and date(a.order_date) <=CURDATE() ) c GROUP BY x
