mapper.select:
SELECT
    a.*,
    b.depart_name as departName,
    c.NAME as supplierName
FROM
    pd_rejected a
    LEFT JOIN sys_depart b ON a.depart_id = b.id
    LEFT JOIN pd_supplier c ON a.supplier_id = c.id

    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.rejected_no LIKE concat('%',#{rejectedNo},'%')


        AND a.supplier_id = #{supplierId}


        AND a.depart_id = #{departId}


        AND a.depart_parent_id = #{departParentId}


        AND a.rejected_date >= #{queryDateStart}


        AND a.rejected_date <= #{queryDateEnd}


order by a.create_time desc
