mapper.select:
SELECT @rowNum :=@rowNum + 1 AS id, rep.* FROM ( SELECT in_table.audit_date, in_table.supplier_id, in_table.supplierName, IFNULL(in_table.inProductNum, 0) AS inProductNum, IFNULL(in_table.inTotalPrice, 0) AS inTotalPrice, IFNULL(do_table.dosageNum, 0) AS dosageNum, IFNULL(do_table.dosagePrice, 0) AS dosagePrice, IFNULL(re_table.rejectedNum, 0) AS rejectedNum, IFNULL(re_table.rejectedPrice, 0) AS rejectedPrice FROM ( SELECT sum(t1.product_num) AS inProductNum, sum(t1.sum_purchase_price) AS inTotalPrice, t1.audit_date, t1.supplier_id, t1.supplierName FROM ( SELECT a.id, a.record_id, b.record_no, b.record_type, b.in_type, b.out_type, a.product_id, a.product_num, a.purchase_price, a.selling_price, a.purchase_price * a.product_num AS sum_purchase_price, date_format(b.audit_date, '%Y-%m') AS audit_date, b.supplier_id, s.`name` AS supplierName FROM pd_stock_record_detail a LEFT JOIN pd_stock_record b ON a.record_id = b.id LEFT JOIN pd_supplier s ON b.supplier_id = s.id LEFT JOIN pd_product pp on a.product_id = pp.id AND b.audit_status = '2' WHERE b.del_flag = '0' AND b.record_type = '1' AND ( b.in_type IS NOT NULL AND b.in_type <> '' ) AND ( b.supplier_id IS NOT NULL AND b.supplier_id <> '' ) AND pp.product_flag = '0'
AND date_format(b.audit_date,'%Y-%m') = #{entity.yearMonth}
AND b.supplier_id = #{entity.querySupplierId}
AND a.depart_parent_id = #{entity.departParentId}
) AS t1 GROUP BY t1.audit_date, t1.supplier_id ) AS in_table LEFT JOIN ( SELECT sum(t2.left_refund_num) AS dosageNum, sum(t2.amount_money) AS dosagePrice, t2.audit_date, t2.supplier_id, t2.supplierName FROM ( SELECT a.id, a.dosage_id, b.dosage_no, a.product_id, a.left_refund_num, a.selling_price, a.amount_money, date_format(b.create_time, '%Y-%m') AS audit_date, ps.supplier_id, s.`name` AS supplierName FROM pd_dosage_detail a LEFT JOIN pd_dosage b ON a.dosage_id = b.id LEFT JOIN pd_product_stock ps ON a.product_stock_id = ps.id LEFT JOIN pd_supplier s ON ps.supplier_id = s.id LEFT JOIN pd_product pp on a.product_id = pp.id WHERE b.del_flag = '0' AND a.left_refund_num > 0 AND ( ps.supplier_id IS NOT NULL AND ps.supplier_id <> '' ) AND pp.product_flag = '0'
AND date_format(b.create_time,'%Y-%m') = #{entity.yearMonth}
AND ps.supplier_id = #{entity.querySupplierId}
AND a.depart_parent_id = #{entity.departParentId}
) AS t2 GROUP BY t2.audit_date, t2.supplier_id ) AS do_table ON in_table.supplier_id = do_table.supplier_id AND in_table.audit_date = do_table.audit_date LEFT JOIN ( SELECT sum(t3.rejected_count) AS rejectedNum, sum(t3.amount_money) AS rejectedPrice, t3.audit_date, t3.supplier_id, t3.supplierName, t3.purchase_price FROM ( SELECT a.id, a.rejected_id, b.rejected_no, a.product_id, a.rejected_count, srd.purchase_price, srd.purchase_price * a.rejected_count AS amount_money, date_format(b.rejected_date, '%Y-%m') AS audit_date, b.supplier_id, s.`name` AS supplierName FROM pd_rejected_detail a LEFT JOIN pd_rejected b ON a.rejected_id = b.id LEFT JOIN pd_supplier s ON b.supplier_id = s.id LEFT JOIN pd_product_stock ps ON a.product_stock_id = ps.id LEFT JOIN pd_stock_record_detail srd ON ps.record_detail_id = srd.id LEFT JOIN pd_product pp on a.product_id = pp.id WHERE b.del_flag = '0' AND a.rejected_count > 0 AND ( b.supplier_id IS NOT NULL AND b.supplier_id <> '' ) AND pp.product_flag = '0'
AND date_format(b.rejected_date,'%Y-%m') = #{entity.yearMonth}
AND b.supplier_id = #{entity.querySupplierId}
AND a.depart_parent_id = #{entity.departParentId}
) AS t3 GROUP BY t3.audit_date, t3.supplier_id ) AS re_table ON in_table.supplier_id = re_table.supplier_id AND in_table.audit_date = re_table.audit_date ) AS rep, (SELECT @rowNum := 0) n ORDER BY rep.audit_date DESC

SELECT a.id, a.record_id, s.`name` AS supplierName, b.audit_date, b.record_no, b.record_type, b.in_type, b.out_type, b.remarks as "remarks", b.supplier_id, pp.number as productNumber, pp.`name` as productName, pp.spec, pp.version, a.batch_no, a.produce_date, a.exp_date, a.product_num, pp.unit_id, pu.`name` AS unitName, a.product_id, a.selling_price, a.purchase_price, a.purchase_price * a.product_num AS inTotalPrice, pv.`name` AS venderName, g. NAME AS distributorName, pp.registration as productRegistration, pp.charge_code, m.invoice_no AS invoiceNo, m.invoice_code AS invoiceCode, m.invoice_data AS invoiceData, pp.jde_code AS jdeCode, pp.dart_code AS dartCode, pp.biding_number as bidingNumber, pv.jde_code AS "venderJdeCode", s.jde_code AS "supplierJdeCode" FROM pd_stock_record_detail a LEFT JOIN pd_stock_record b ON a.record_id = b.id LEFT JOIN pd_supplier s ON b.supplier_id = s.id LEFT JOIN pd_product pp ON a.product_id = pp.id LEFT JOIN pd_unit pu ON pp.unit_id = pu.id LEFT JOIN pd_vender pv ON pp.vender_id = pv.id LEFT JOIN pd_distributor g ON a.distributor_id = g.id LEFT JOIN pd_invoice_detail l ON l.bill_detail_id = a.id LEFT JOIN pd_invoice m ON m.id = l.invoice_id AND b.audit_status = '2' WHERE b.del_flag = '0' AND b.record_type = '1' AND pp.product_flag = '0' AND ( b.in_type IS NOT NULL AND b.in_type <> '' ) AND ( b.supplier_id IS NOT NULL AND b.supplier_id <> '' )
AND date_format(b.audit_date,'%Y-%m') = #{entity.yearMonth}
AND b.supplier_id = #{entity.supplierId}
AND a.depart_parent_id = #{entity.departParentId}
AND date_format( a.exp_date, '%Y-%m-%d') >= #{entity.queryExpDateStart}
AND date_format( a.exp_date, '%Y-%m-%d') <= #{entity.queryExpDateEnd}
AND a.batch_no LIKE concat('%',#{entity.batchNo},'%')
AND b.record_no LIKE concat('%',#{entity.recordNo},'%')
AND date_format( b.audit_date, '%Y-%m-%d') >= #{entity.queryDateStart}
AND date_format( b.audit_date, '%Y-%m-%d') <= #{entity.queryDateEnd}
AND b.distributor_id = #{entity.distributorId}
AND pp.number LIKE concat('%',#{entity.productNumber},'%')
AND pp.vender_id = #{entity.venderId}
AND pp.spec LIKE concat('%',#{entity.spec},'%')
AND pp.version LIKE concat('%',#{entity.version},'%')
AND pp.registration LIKE concat('%',#{entity.registration},'%')
AND ( pp.number LIKE concat('%',#{entity.productName},'%') or pp.name LIKE concat('%',#{entity.productName},'%') or pp.py LIKE concat('%',#{entity.productName},'%') or pp.wb LIKE concat('%',#{entity.productName},'%') or pp.bname LIKE concat('%',#{entity.productName},'%') or pp.bpy LIKE concat('%',#{entity.productName},'%') or pp.bwb LIKE concat('%',#{entity.productName},'%') or pp.zdy LIKE concat('%',#{entity.productName},'%') )

SELECT a.id, a.dosage_id, s.`name` AS supplierName, b.dosage_date, b.dosage_no, b.remarks as "remarks", a.product_id, a.left_refund_num, a.selling_price , a.selling_price * a.left_refund_num AS dosagePrice, ps.supplier_id, pp.number as productNumber, pp.`name` as productName, pp.spec, pp.version, a.batch_no, a.exp_date, pp.unit_id, pu.`name` AS unitName, pv.`name` AS venderName, pp.registration as productRegistration, pp.charge_code, pp.jde_code AS jdeCode, pp.dart_code AS dartCode, pp.biding_number as bidingNumber, pv.jde_code AS "venderJdeCode", s.jde_code AS "supplierJdeCode" FROM pd_dosage_detail a LEFT JOIN pd_dosage b ON a.dosage_id = b.id LEFT JOIN pd_product_stock ps on a.product_stock_id = ps.id LEFT JOIN pd_supplier s ON ps.supplier_id = s.id LEFT JOIN pd_product pp ON a.product_id = pp.id LEFT JOIN pd_unit pu ON pp.unit_id = pu.id LEFT JOIN pd_vender pv ON pp.vender_id = pv.id WHERE b.del_flag = '0' AND pp.product_flag = '0' AND a.left_refund_num >0 AND ( ps.supplier_id IS NOT NULL AND ps.supplier_id <>'' )
AND date_format(b.create_time,'%Y-%m') = #{entity.yearMonth}
AND ps.supplier_id = #{entity.supplierId}
AND a.depart_parent_id = #{entity.departParentId}
AND date_format( a.exp_date, '%Y-%m-%d') >= #{entity.queryExpDateStart}
AND date_format( a.exp_date, '%Y-%m-%d') <= #{entity.queryExpDateEnd}
AND a.batch_no LIKE concat('%',#{entity.batchNo},'%')
AND b.dosage_no LIKE concat('%',#{entity.dosageNo},'%')
AND date_format( b.create_time, '%Y-%m-%d') >= #{entity.queryDateStart}
AND date_format( b.create_time, '%Y-%m-%d') <= #{entity.queryDateEnd}
AND pp.number LIKE concat('%',#{entity.productNumber},'%')
AND pp.vender_id = #{entity.venderId}
AND pp.spec LIKE concat('%',#{entity.spec},'%')
AND pp.version LIKE concat('%',#{entity.version},'%')
AND pp.registration LIKE concat('%',#{entity.registration},'%')
AND ( pp.number LIKE concat('%',#{entity.productName},'%') or pp.name LIKE concat('%',#{entity.productName},'%') or pp.py LIKE concat('%',#{entity.productName},'%') or pp.wb LIKE concat('%',#{entity.productName},'%') or pp.bname LIKE concat('%',#{entity.productName},'%') or pp.bpy LIKE concat('%',#{entity.productName},'%') or pp.bwb LIKE concat('%',#{entity.productName},'%') or pp.zdy LIKE concat('%',#{entity.productName},'%') )

SELECT a.id, a.rejected_id, s.`name` AS supplierName, b.rejected_date, b.rejected_no, b.remarks as "remarks", a.product_id, a.rejected_count, srd.purchase_price, srd.purchase_price * a.rejected_count AS amount_money, ps.supplier_id, pp.number as productNumber, pp.`name` as productName, pp.spec, pp.version, a.batch_no, ps.exp_date, pp.unit_id, pu.`name` AS unitName, pv.`name` AS venderName, pp.registration as productRegistration, pp.charge_code, pp.jde_code AS jdeCode, pp.dart_code AS dartCode, pp.biding_number as bidingNumber, pv.jde_code AS "venderJdeCode", s.jde_code AS "supplierJdeCode" FROM pd_rejected_detail a LEFT JOIN pd_rejected b ON a.rejected_id = b.id LEFT JOIN pd_supplier s on b.supplier_id = s.id LEFT JOIN pd_product_stock ps on a.product_stock_id = ps.id LEFT JOIN pd_stock_record_detail srd on ps.record_detail_id = srd.id LEFT JOIN pd_product pp ON a.product_id = pp.id LEFT JOIN pd_unit pu ON pp.unit_id = pu.id LEFT JOIN pd_vender pv ON pp.vender_id = pv.id WHERE b.del_flag = '0' AND a.rejected_count >0 AND ( b.supplier_id IS NOT NULL AND b.supplier_id <> '' ) AND pp.product_flag = '0'
AND date_format(b.create_time,'%Y-%m') = #{entity.yearMonth}
AND b.supplier_id = #{entity.supplierId}
AND a.depart_parent_id = #{entity.departParentId}
AND date_format( ps.exp_date, '%Y-%m-%d') >= #{entity.queryExpDateStart}
AND date_format( ps.exp_date, '%Y-%m-%d') <= #{entity.queryExpDateEnd}
AND a.batch_no LIKE concat('%',#{entity.batchNo},'%')
AND b.rejected_no LIKE concat('%',#{entity.rejectedNo},'%')
AND date_format( b.create_time, '%Y-%m-%d') >= #{entity.queryDateStart}
