AND ( pp.number LIKE concat('%',#{entity.productName},'%') or pp.name LIKE concat('%',#{entity.productName},'%') or pp.py LIKE concat('%',#{entity.productName},'%') or pp.wb LIKE concat('%',#{entity.productName},'%') or pp.bname LIKE concat('%',#{entity.productName},'%') or pp.bpy LIKE concat('%',#{entity.productName},'%') or pp.bwb LIKE concat('%',#{entity.productName},'%') or pp.zdy LIKE concat('%',#{entity.productName},'%') )
