) t1 GROUP BY t1.audit_date, t1.depart_id ) do_table2 LEFT JOIN sys_depart de ON de.id = do_table2.depart_id ) do_table2 ON do_table1.depart_id = do_table2.depart_id AND do_table1.audit_date = do_table2.audit_date LEFT JOIN ( SELECT do_table3.audit_date, do_table3.depart_id, IFNULL(do_table3.dosageNum, 0) AS dosageNum, IFNULL(do_table3.dosagePrice, 0) AS dosagePrice, de.depart_name FROM ( SELECT t1.audit_date, sum(t1.left_refund_num) AS dosageNum, sum(t1.dosagePrice) AS dosagePrice, t1.depart_id, t1.dosage_no FROM ( SELECT date_format(b.dosage_date, '%Y-%m') AS audit_date, b.depart_id, a.left_refund_num, IFNULL(a.selling_price, 0) AS sellingPrice, a.left_refund_num * IFNULL(a.selling_price, 0) AS dosagePrice, b.dosage_no FROM pd_dosage_detail a LEFT JOIN pd_dosage b ON a.dosage_id = b.id WHERE a.del_flag = '0' AND a.left_refund_num > 0 AND a.hy_charged != '0'
AND b.depart_parent_id = #{entity.departParentId}
AND b.depart_id = #{entity.departId}
AND date_format(b.dosage_date,'%Y-%m') = #{entity.yearMonth}
and b.depart_id IN
#{item}
) t1 GROUP BY t1.audit_date, t1.depart_id ) do_table3 LEFT JOIN sys_depart de ON de.id = do_table3.depart_id ) do_table3 ON do_table1.depart_id = do_table3.depart_id AND do_table1.audit_date = do_table3.audit_date LEFT JOIN sys_depart de ON de.id = do_table1.depart_id ) AS rep, (SELECT @rowNum := 0) n ORDER BY rep.audit_date DESC

