select c.type as "type", ROUND(sum(c.y),2) as y, ROUND(sum(c.x),2) as x, ROUND(sum(c.s),2) as s, ROUND(sum(c.y),2) as 采购金额, ROUND(sum(c.x),2) as 收费金额, ROUND(sum(c.s),2) as 不可收费金额 from( SELECT DATE_FORMAT(x.Month, '%Y-%m') as "type" , "0" as "y","0" as "x", "" as "s" FROM XNumber x where x.Month > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) and substr(x.Month,1,7) <=substr(CURDATE(),1,7) UNION ALL select a.month as "type", a.in_record_price as "y", "" as "x", "" as "s" from pd_numerical_inf a left join sys_depart t on t.id=a.depart_id where a.tj_type='0'
AND a.depart_id IN
#{item}
and t.depart_type in('1')
AND a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) AND substr(a.create_time,1,7) <=substr(CURDATE(),1,7) UNION ALL select DATE_FORMAT(e.create_time, '%Y-%m') AS "type", "" AS "y", (d.selling_price * d.dosage_count) AS "x", "" as "s" from pd_dosage_detail d left join pd_dosage e on e.id=d.dosage_id where d.hy_charged='0' and d.del_flag='0'
AND e.depart_id IN
#{item}
AND e.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) AND substr(e.create_time,1,7) <=substr(CURDATE(),1,7)
UNION ALL select DATE_FORMAT(e.create_time, '%Y-%m') AS "type", "" AS "y", "" AS "x", (d.selling_price * d.dosage_count) as "s" from pd_dosage_detail d left join pd_dosage e on e.id=d.dosage_id where d.hy_charged='1' and d.del_flag='0'
AND e.depart_id IN
#{item}
AND e.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) AND substr(e.create_time,1,7) <=substr(CURDATE(),1,7) ) c GROUP BY type;

select c.type as "type", ROUND(sum(c.y),2) as "y", ROUND(sum(c.x),2) as "x", ROUND(sum(c.s),2) as "s", ROUND(sum(c.y),2) as 采购金额 , ROUND(sum(c.x),2) as 收费金额, ROUND(sum(c.s),2) as 不可收费金额 from( select t.depart_name as "type", a.in_record_price as "y", "" as "x", "" as "s" from pd_numerical_inf a left join sys_depart t on t.id=a.depart_id where a.tj_type='0' and t.depart_type in('2')
AND a.depart_id IN
#{item}
AND a.month = #{entity.yearMonth}
