AND e.depart_id IN
#{item}
AND DATE_FORMAT(e.create_time, '%Y-%m') = #{entity.yearMonth}
union all select c.depart_name as "type", "" as "y", d.selling_price * d.dosage_count as "x", "" as "s" from pd_dosage_detail d left join pd_dosage e on e.id=d.dosage_id left join sys_depart c on c.id in( (select t.parent_id from sys_depart t where t.id=e.depart_id and t.depart_type in('3'))) where d.hy_charged='0' and d.del_flag='0' and c.depart_type in('2') and c.depart_name is not null
AND e.depart_id IN
#{item}
AND DATE_FORMAT(e.create_time, '%Y-%m') = #{entity.yearMonth}
union all select c.depart_name as "type" , "" as "y", "" as "x", d.selling_price * d.dosage_count as "s" from pd_dosage_detail d left join pd_dosage e on e.id=d.dosage_id left join sys_depart c on c.id=e.depart_id where d.hy_charged='1' and d.del_flag='0' and c.depart_type in('2')
AND e.depart_id IN
#{item}
AND DATE_FORMAT(e.create_time, '%Y-%m') = #{entity.yearMonth}
union all select c.depart_name as "type", "" as "y", "" as "x", d.selling_price * d.dosage_count as "s" from pd_dosage_detail d left join pd_dosage e on e.id=d.dosage_id left join sys_depart c on c.id in( (select t.parent_id from sys_depart t where t.id=e.depart_id and t.depart_type in('3'))) where d.hy_charged='1' and d.del_flag='0' and e.del_flag='0' and c.depart_type in('2') and c.depart_name is not null
AND e.depart_id IN
#{item}
AND DATE_FORMAT(e.create_time, '%Y-%m') = #{entity.yearMonth}
) c group by c.type order by y desc limit 0,10

