AND b.in_depart_id IN
#{item}
AND b.record_type='1' AND b.in_type='1' AND t.depart_type='1'
AND DATE_FORMAT(b.create_time, '%Y-%m') = #{entity.yearMonth}
group by is_charge

select t.depart_name as "name", ROUND(sum(a.in_record_price),2) as value from pd_numerical_inf a left join sys_depart t on t.id=a.depart_id where a.tj_type='0' and t.depart_type='2'
AND a.depart_id IN
#{item}
AND a.month = #{entity.yearMonth}
group by a.depart_id order by value desc limit 0,10

select ROUND(sum(c.count),2) as value, c.item as "name" from( select (a.dosage_count * a.selling_price) as "count", d.depart_name as "item" from pd_dosage_detail a left join pd_dosage b on b.id=a.dosage_id left join pd_product c on c.id=a.product_id left join sys_depart d on d.id=b.depart_id where a.hy_charged='0' and b.del_flag='0' and a.del_flag='0'
AND DATE_FORMAT(b.create_time, '%Y-%m') = #{entity.yearMonth}
AND b.depart_id IN
#{item}
and d.depart_type in('2')
) c group by c.item order by c.count desc limit 0,10

select t.depart_name as "departName",
ROUND(SUM(a.in_record_price),2) as "amount"
from pd_numerical_inf a
left join sys_depart t on a.depart_id=t.id
where 1=1

    AND tj_type=#{entity.productFlag}


    AND t.depart_type=#{entity.departType}

AND a.month= #{entity.yearMonth}
group by a.depart_id
order by a.in_record_price desc limit 0,10

select t.depart_name as "departName", sum(case when month=#{entity.lastMonth} then in_record_price else 0 end ) as "lastPrice", sum(case when month=#{entity.theLastYearMonth} then in_record_price else 0 end ) as "theLastPrice", sum(case when month=#{entity.theYearMonth} then in_record_price else 0 end ) as "thePrice" from pd_numerical_inf a left join sys_depart t on a.depart_id=t.id where 1=1
AND a.tj_type = #{entity.productFlag}
AND a.depart_id IN
#{item}
group by t.id order by in_record_price desc

select c.*, (c.janPrice+c.febPrice+c.marPrice+c.aprPrice+c.mayPrice +c.junPrice+c.julPrice+c.augPrice+c.septPrice+c.octPrice +c.novPrice+c.decPrice) as "countPrice" from ( select a.id, t.depart_name as "departName", sum(case when month=concat(#{entity.year},'-01') then in_record_price else 0 end ) as "janPrice", sum(case when month=concat(#{entity.year},'-02') then in_record_price else 0 end ) as "febPrice", sum(case when month=concat(#{entity.year},'-03') then in_record_price else 0 end ) as "marPrice", sum(case when month=concat(#{entity.year},'-04') then in_record_price else 0 end ) as "aprPrice", sum(case when month=concat(#{entity.year},'-05') then in_record_price else 0 end ) as "mayPrice", sum(case when month=concat(#{entity.year},'-06') then in_record_price else 0 end ) as "junPrice", sum(case when month=concat(#{entity.year},'-07') then in_record_price else 0 end ) as "julPrice", sum(case when month=concat(#{entity.year},'-08') then in_record_price else 0 end ) as "augPrice", sum(case when month=concat(#{entity.year},'-09') then in_record_price else 0 end ) as "septPrice", sum(case when month=concat(#{entity.year},'-10') then in_record_price else 0 end ) as "octPrice", sum(case when month=concat(#{entity.year},'-11') then in_record_price else 0 end ) as "novPrice", sum(case when month=concat(#{entity.year},'-12') then in_record_price else 0 end ) as "decPrice" from pd_numerical_inf a left join sys_depart t on a.depart_id=t.id where 1=1
AND a.tj_type = #{entity.productFlag}
AND t.id IN
#{item}
group by t.id order by in_record_price desc ) c

SELECT xx.`month` as yearMonth , sum(xx.amount) as amount FROM ( SELECT DATE_FORMAT(x. MONTH, '%Y-%m') AS "month", 0 AS "amount" FROM XNumber x WHERE x. MONTH > DATE_SUB(CURDATE(), INTERVAL 2 YEAR) AND substr(x. MONTH, 1, 7) <= substr(CURDATE(), 1, 7) UNION ALL SELECT a.`month`, ROUND(SUM(a.in_record_price), 2) AS "amount" FROM pd_numerical_inf a LEFT JOIN sys_depart t ON a.depart_id = t.id WHERE 1 = 1 AND t.depart_type = '1'
AND t.depart_parent_id = #{entity.departParentId}
GROUP BY a. MONTH ) xx GROUP BY xx. MONTH ORDER BY xx. MONTH
