mapper.select:
select a.product_id as "productId", c.number as "number", c.name as "productName", c.product_flag as "productFlag", case when c.product_flag = '0' then "耗材" when c.product_flag = '1' then "试剂" end as "productFlagName", c.spec as "spec", c.biding_number as "bidingNumber", d.name as "unitName", g.stock_num as "stockNum", sum(a.product_num) as "productNum", sum(a.product_num) as "autoNum", e.id as "supplierId", e.name as "supplierName", f.name as "venderName", c.charge_code as "chargeCode", c.purchase_price as "purchasePrice", c.selling_price as "sellingPrice", c.registration as "registration" from pd_stock_record_detail a left join pd_stock_record b on a.record_id=b.id left join pd_product c on c.id=a.product_id left join pd_product_stock_total g on g.product_id=a.product_id and g.depart_id=#{departId} left join pd_unit d on d.id=c.unit_id left join pd_supplier e on e.id=c.supplier_id left join pd_vender f on f.id=c.vender_id where PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format( b.submit_date, '%Y%m' ) ) =1
AND b.audit_status = #{auditStatus}
AND c.product_flag = #{productFlag}
AND b.record_type = #{recordType}
AND b.depart_parent_id = #{departParentId}
AND b.out_depart_id = #{outDepartId}
AND ( b.name LIKE concat('%',#{productName},'%') or c.py LIKE concat('%',#{productName},'%') or c.wb LIKE concat('%',#{productName},'%') or c.bname LIKE concat('%',#{productName},'%') or c.bpy LIKE concat('%',#{productName},'%') or c.bwb LIKE concat('%',#{productName},'%') or c.zdy LIKE concat('%',#{productName},'%') )
AND c.number LIKE concat('%',#{number},'%')
AND c.biding_number LIKE concat('%',#{bidingNumber},'%')
AND c.spec LIKE concat('%',#{spec},'%')
AND c.registration LIKE concat('%',#{registration},'%')
AND c.charge_code LIKE concat('%',#{chargeCode},'%')
AND c.supplier_id =#{supplierId}
AND c.vender_id =#{venderId}
GROUP BY a.product_id

