mapper.select:
SELECT , , , , ,
FROM pd_stock_record_detail a
WHERE a.del_flag =#{DEL_FLAG_NORMAL} and record_id = #{recordId}

SELECT
,
,
,
,
,
,

FROM pd_stock_record_detail a








	a.del_flag =#{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.exp_date >= #{queryExpDateStart}


		AND a.exp_date <= #{queryExpDateEnd}


		AND a.batch_no LIKE concat('%',#{batchNo},'%')


		AND g.record_no LIKE concat('%',#{recordNo},'%')


		AND g.submit_date >= #{queryInDateStart}


		AND g.submit_date <= #{queryInDateEnd}


		AND g.audit_status = #{auditStatus}


		AND g.submit_status = #{submitStatus}


		AND a.product_id = #{productId}


		AND g.record_type = #{recordType}


		AND g.supplier_id = #{supplierId}


		AND g.in_type = #{inType}


		AND ((g.record_type = '2' AND g.out_depart_id = #{deptId})
		OR (g.record_type = '1' AND g.in_depart_id =#{deptId}))


		AND (
				(g.record_type = '2'
					AND g.out_depart_id in

						#{id}

				)
			OR (g.record_type = '1'
					AND g.in_depart_id in

						#{id}

				)
			)


		AND a.depart_id = #{departId}


		AND a.depart_parent_id = #{departParentId}


		AND c.number LIKE concat('%',#{productNumber},'%')


		AND c.vender_id = #{venderId}


		AND c.spec LIKE concat('%',#{spec},'%')


		AND c.version LIKE concat('%',#{version},'%')


		AND c.registration LIKE concat('%',#{registration},'%')


		AND (
		c.number LIKE concat('%',#{productName},'%')
		or c.name LIKE concat('%',#{productName},'%')
		or c.py LIKE concat('%',#{productName},'%')
		or c.wb LIKE concat('%',#{productName},'%')
		or c.bname LIKE concat('%',#{productName},'%')
		or c.bpy LIKE concat('%',#{productName},'%')
		or c.bwb LIKE concat('%',#{productName},'%')
		or c.zdy LIKE concat('%',#{productName},'%')
		)

	ORDER BY create_time desc

mapper.update:
UPDATE pd_stock_record_detail SET
	in_huowei_code = #{inHuoweiCode}
WHERE in_huowei_code= #{oldInHuoweiCode}

UPDATE pd_stock_record_detail SET
	out_huowei_code = #{outHuoweiCode}
WHERE out_huowei_code= #{oldOutHuoweiCode}
