mapper.select:
select
  ,
  b.depart_name as inDepartName,
  c.depart_name as outDepartName,
  d.name as supplierName,
  e.realname as submitByName,
  e.realname as auditByName
from
  pd_stock_record a
  left join sys_depart b on a.in_depart_id = b.id
  left join sys_depart c on a.out_depart_id = b.id
  left join pd_supplier d on a.supplier_id = d.id
  left join sys_user e on a.submit_by = e.id
  left join sys_user f on a.audit_by = f.id

      a.del_flag = #{DEL_FLAG_NORMAL}

          AND a.record_no = #{recordNo}


          AND a.record_type = #{recordType}


          AND a.out_type = #{outType}


          AND a.in_type = #{inType}


          AND a.order_no = #{orderNo}


          AND a.allocation_no = #{allocationNo}


          AND a.apply_no = #{applyNo}


          AND a.dosagert_no = #{dosagertNo}



          AND a.submit_by = #{submitBy}



          AND a.submit_status = #{submitStatus}


          AND a.out_depart_id = #{outDepartId}


          AND a.in_depart_id = #{inDepartId}


          AND a.supplier_id = #{supplierId}


          AND a.depart_id = #{departId}


          AND a.depart_parent_id = #{departParentId}


  order by a.create_time desc

select , b.depart_name as inDepartName, c.depart_name as outDepartName, d.name as supplierName, e.realname as submitByName, e.realname as auditByName from pd_stock_record a left join sys_depart b on a.in_depart_id = b.id left join sys_depart c on a.out_depart_id = b.id left join pd_supplier d on a.supplier_id = d.id left join sys_user e on a.submit_by = e.id left join sys_user f on a.audit_by = f.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.record_no = #{recordNo}

SELECT a.record_no as "recordNo", a.submit_date as "submitDate", c.depart_name as "outDepartName", d.depart_name as "inDepartName", pp.name as "productName", pp.number as "number", pp.spec as "spec", pp.version as "version", srp.product_bar_code as "productBarCode", srp.batch_no as "batchNo", srp.exp_date as "expDate", srp.product_num as "productNum", srp.remarks as "remarks", e.realname as "submitByName" FROM pd_stock_record a LEFT JOIN pd_stock_record_detail srp ON a.id = srp.record_id LEFT JOIN pd_product pp ON srp.product_id = pp.id LEFT JOIN pd_stock_record sr ON a.allocation_no = sr.allocation_no LEFT JOIN sys_depart c ON a.out_depart_id = c.id LEFT JOIN sys_depart d ON a.in_depart_id = d.id LEFT JOIN sys_user e on a.submit_by = e.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.id != sr.id
AND a.record_type = #{recordType}
AND a.in_depart_id = #{inDepartId}
AND a.audit_status = #{auditStatus}
AND a.in_type = #{inType}
ORDER BY a.update_time DESC, a.submit_date DESC
