mapper.select:
select
,
b.depart_name as inDepartName,
c.depart_name as outDepartName,
d.name as supplierName,
e.realname as submitByName,
f.realname as auditByName,
g.name as distributorName
from
pd_stock_record a
left join sys_depart b on a.in_depart_id = b.id
left join sys_depart c on a.out_depart_id = c.id
left join pd_supplier d on a.supplier_id = d.id
left join sys_user e on a.submit_by = e.id
left join sys_user f on a.audit_by = f.id
left join pd_distributor g on a.distributor_id = g.id

    a.del_flag = #{entity.DEL_FLAG_NORMAL}

        AND a.record_no LIKE concat('%',#{entity.recordNo},'%')


        AND a.record_type = #{entity.recordType}


        AND a.out_type = #{entity.outType}


        AND a.out_type = '4'


        AND a.out_type <> '4'


        AND a.in_type = #{entity.inType}


        AND a.audit_status = #{entity.auditStatus}


        AND a.order_no = #{entity.orderNo}


        AND a.merge_order_no = #{entity.mergeOrderNo}


        AND a.allocation_no = #{entity.allocationNo}


        AND a.apply_no = #{entity.applyNo}


        AND a.dosagert_no = #{entity.dosagertNo}


        AND a.submit_by = #{entity.submitBy}


        AND a.submit_status = #{entity.submitStatus}


        AND a.out_depart_id = #{entity.outDepartId}


        AND a.in_depart_id = #{entity.inDepartId}


        AND a.supplier_id = #{entity.supplierId}


        AND a.distributor_id = #{entity.distributorId}


        AND a.depart_id = #{entity.departId}


        AND a.depart_parent_id = #{entity.departParentId}


        AND date_format( a.submit_date, '%Y-%m-%d') >= #{entity.queryDateStart}


        AND date_format( a.submit_date, '%Y-%m-%d') <= #{entity.queryDateEnd}


order by a.create_time desc

select , b.depart_name as inDepartName, c.depart_name as outDepartName, d.name as supplierName, e.realname as submitByName, f.realname as auditByName, g.name as distributorName from pd_stock_record a left join sys_depart b on a.in_depart_id = b.id left join sys_depart c on a.out_depart_id = c.id left join pd_supplier d on a.supplier_id = d.id left join sys_user e on a.submit_by = e.id left join sys_user f on a.audit_by = f.id left join pd_distributor g on a.distributor_id = g.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.record_no = #{recordNo}

SELECT DISTINCT a.record_no as "recordNo", a.submit_date as "submitDate", c.depart_name as "outDepartName", d.depart_name as "inDepartName", pp.name as "productName", pp.number as "number", pp.spec as "spec", pp.version as "version", srp.product_bar_code as "productBarCode", srp.batch_no as "batchNo", srp.exp_date as "expDate", srp.product_num as "productNum", srp.remarks as "remarks", e.realname as "submitByName" FROM pd_stock_record a LEFT JOIN pd_stock_record_detail srp ON a.id = srp.record_id LEFT JOIN pd_product pp ON srp.product_id = pp.id LEFT JOIN pd_stock_record sr ON a.allocation_no = sr.allocation_no LEFT JOIN sys_depart c ON a.out_depart_id = c.id LEFT JOIN sys_depart d ON a.in_depart_id = d.id LEFT JOIN sys_user e on a.submit_by = e.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.id != sr.id
AND a.record_type = #{recordType}
AND a.in_depart_id = #{inDepartId}
AND a.audit_status = #{auditStatus}
AND a.in_type = #{inType}
AND a.depart_parent_id = #{departParentId}
ORDER BY a.update_time DESC, a.submit_date DESC

select b.id as "productId", b.number as "number", b.name as "productName", b.biding_number as "bidingNumber", b.spec as "spec", case when b.product_flag = '0' then "耗材" when b.product_flag = '1' then "试剂" end as "productFlagName", sum(a.product_num) as "totalSum", sum(a.purchase_price * a.product_num) as "inTotalPrice", c.name as "venderName", d.name as "supplierName", e.name as "unitName", g.name as "distributorName" from pd_stock_record_detail a left join pd_stock_record f on a.record_id=f.id and f.del_flag=#{entity.DEL_FLAG_NORMAL} left join pd_product b on a.product_id=b.id left join pd_vender c on b.vender_id=c.id left join pd_supplier d on b.supplier_id=d.id left join pd_unit e on b.unit_id=e.id left join pd_distributor g on a.distributor_id = g.id where a.del_flag=#{entity.DEL_FLAG_NORMAL} and a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) and substr(a.create_time,1,7) <=substr(CURDATE(),1,7)
AND f.depart_parent_id = #{entity.departParentId}
AND f.in_depart_id = #{entity.inDepartId}
AND ( b.number LIKE concat('%',#{entity.productName},'%') or b.name LIKE concat('%',#{entity.productName},'%') or b.py LIKE concat('%',#{entity.productName},'%') or b.wb LIKE concat('%',#{entity.productName},'%') or b.bname LIKE concat('%',#{entity.productName},'%') or b.bpy LIKE concat('%',#{entity.productName},'%') or b.bwb LIKE concat('%',#{entity.productName},'%') or b.zdy LIKE concat('%',#{entity.productName},'%') )
AND b.number LIKE concat('%',#{entity.number},'%')
AND a.supplier_id = #{entity.supplierId}
AND a.distributor_id = #{entity.distributorId}
AND b.spec LIKE concat('%',#{entity.spec},'%')
AND b.product_flag = #{entity.productFlag}
AND b.vender_id = #{entity.venderId}
AND b.registration LIKE concat('%',#{entity.registration},'%')
AND f.record_type = #{entity.recordType}
AND f.audit_status = #{entity.auditStatus}
AND DATE_FORMAT(a.create_time, '%Y-%m-%d') >= #{entity.queryDateStart}
AND DATE_FORMAT(a.create_time, '%Y-%m-%d') <= #{entity.queryDateEnd}
GROUP BY b.number

select
c.type as "type",
ROUND(sum(c.y),2) as "金额"
from(
SELECT DATE_FORMAT(x.Month, '%Y-%m') as "type" ,
"0" as "y"
FROM XNumber x
where x.Month > DATE_SUB(CURDATE(), INTERVAL 1 YEAR)
and substr(x.Month,1,7) <=substr(CURDATE(),1,7)
UNION ALL
SELECT DATE_FORMAT(a.create_time, '%Y-%m') AS "type",
a.product_num * a.purchase_price AS "y"
FROM pd_stock_record_detail a
left join pd_stock_record b on a.record_id=b.id and b.del_flag='0'
where b.record_type ='1'
AND b.in_type ='1'
AND b.audit_status ='2'
AND a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR)
AND substr(a.create_time,1,7) <=substr(CURDATE(),1,7)

    and a.product_id=#{productId}


    AND b.depart_parent_id = #{departParentId}


    AND b.in_depart_id = #{inDepartId}


    AND DATE_FORMAT(a.create_time, '%Y-%m-%d') >= #{queryDateStart}


    AND DATE_FORMAT(a.create_time, '%Y-%m-%d') <= #{queryDateEnd}

) c
GROUP BY type;

select
c.type as "type",
ROUND(sum(c.y),2) as "数量"
from(
SELECT DATE_FORMAT(x.Month, '%Y-%m') as "type" ,
"0" as "y"
FROM XNumber x
where x.Month > DATE_SUB(CURDATE(), INTERVAL 1 YEAR)
and substr(x.Month,1,7) <=substr(CURDATE(),1,7)
UNION ALL
SELECT DATE_FORMAT(a.create_time, '%Y-%m') AS "type",a.product_num AS "y"
FROM pd_stock_record_detail a
left join pd_stock_record b on a.record_id=b.id and b.del_flag='0'
where
b.record_type ='1'
AND b.in_type ='1'
AND b.audit_status ='2'
AND a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR)
AND substr(a.create_time,1,7) <=substr(CURDATE(),1,7)

    and a.product_id=#{productId}


    AND b.depart_parent_id = #{departParentId}


    AND b.in_depart_id = #{inDepartId}


    AND DATE_FORMAT(a.create_time, '%Y-%m-%d') >= #{queryDateStart}


    AND DATE_FORMAT(a.create_time, '%Y-%m-%d') <= #{queryDateEnd}

) c
GROUP BY type;

SELECT a.id AS "id", a.in_depart_id AS "kfId", pp.id AS "productId", pp.number AS "productNo", pp.name AS "productName", pp.spec AS "productSpec", pp.version AS "productVersion", pp.unit_id AS "unit", pp.product_flag AS "productType", vd.name AS "venderName", pp.purchase_price AS "pruPrice", srp.product_num AS "stockNum", srp.batch_no AS "batchNo", srp.exp_date AS "validDate" FROM pd_stock_record a left join pd_stock_record_detail srp ON a.id = srp.record_id left join pd_product pp on srp.product_id = pp.id LEFT JOIN pd_supplier su on pp.supplier_id = su.id LEFT JOIN pd_vender vd on pp.vender_id = vd.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.in_depart_id = #{storeroomId}
AND a.record_no = #{recordNo}
AND a.record_type = #{recordType}
AND a.audit_status = #{auditStatus}

select d.id as "supplierId", d.name as "supplierName", c.id as "productId", c.name as "productName", c.number as "number", c.product_flag as "productFlag", case when c.product_flag = '0' then "耗材" when c.product_flag = '1' then "试剂" end as "productFlagName", c.spec as "spec", c.biding_number as "bidingNumber", c.category_one as "categoryOne", c.registration as "registration", f.name as "unitName", a.purchase_price as "purchasePrice", IFNULL(a.purchase_price * sum(product_num) ,0) as "inTotalPrice", sum(product_num) as "productNum", g.stock_num as "stockNum", IFNULL(g.stock_num * a.purchase_price ,0) as "stockPrice", e.name as "venderName" from pd_stock_record_detail a left join pd_stock_record b on a.record_id=b.id left join pd_product c on c.id=a.product_id left join pd_supplier d on d.id=a.supplier_id left join pd_vender e on e.id=c.vender_id left join pd_unit f on f.id=c.unit_id left join pd_product_stock_total g on g.product_id=a.product_id
AND g.depart_id = #{entity.departId}
where 1=1
AND b.in_depart_id = #{entity.inDepartId}
AND b.record_type = #{entity.recordType}
AND b.audit_status =#{entity.auditStatus}
AND b.depart_parent_id = #{entity.departParentId}
AND b.audit_status = #{entity.auditStatus}
AND ( c.number LIKE concat('%',#{entity.productName},'%') or c.name LIKE concat('%',#{entity.productName},'%') or c.py LIKE concat('%',#{entity.productName},'%') or c.wb LIKE concat('%',#{entity.productName},'%') or c.bname LIKE concat('%',#{entity.productName},'%') or c.bpy LIKE concat('%',#{entity.productName},'%') or c.bwb LIKE concat('%',#{entity.productName},'%') or c.zdy LIKE concat('%',#{entity.productName},'%') )
AND c.number LIKE concat('%',#{entity.number},'%')
AND c.product_flag =#{entity.productFlag}
AND c.spec LIKE concat('%',#{entity.spec},'%')
AND c.registration LIKE concat('%',#{entity.registration},'%')
AND c.biding_number LIKE concat('%',#{entity.bidingNumber},'%')
AND a.supplier_id =#{entity.supplierId}
AND c.category_one =#{entity.categoryOne}
AND c.vender_id =#{entity.venderId}
AND date_format( b.submit_date, '%Y-%m-%d') >= #{entity.queryDateStart}
AND date_format( b.submit_date, '%Y-%m-%d') <= #{entity.queryDateEnd}
GROUP BY a.supplier_id,c.id order by d.id desc

select
sum(product_num) as "outProductNum",
IFNULL(sum(product_num) * purchase_price ,0) as "outTotalPrice"
from pd_stock_record_detail a
left join pd_stock_record b on a.record_id=b.id
where 1=1

    AND b.out_depart_id = #{outDepartId}


    AND b.record_type = #{recordType}


    AND b.out_type = #{outType}


    AND a.product_id = #{productId}


    AND a.supplier_id = #{supplierId}


    AND a.distributor_id = #{distributorId}

SELECT in_table.audit_date, in_table.depart_id, in_table.inProductNum, in_table.inTotalPrice, out_table.outProductNum, out_table.outTotalPrice, de.depart_name FROM ( SELECT sum( t1.product_num ) AS inProductNum, sum( t1.sum_purchase_price ) AS inTotalPrice, t1.audit_date, t1.depart_id FROM ( SELECT a.id, a.record_id, b.record_no, b.record_type, b.in_type, b.out_type, a.product_id, a.product_num, a.purchase_price, a.selling_price, a.purchase_price * a.product_num AS sum_purchase_price, date_format( b.audit_date, '%Y-%m' ) AS audit_date, b.in_depart_id AS depart_id FROM pd_stock_record_detail a LEFT JOIN pd_stock_record b ON a.record_id = b.id AND b.audit_status = '2' WHERE b.del_flag = '0' AND b.record_type = '1' AND ( b.in_type IS NOT NULL AND in_type <> '' )
AND b.depart_parent_id = #{entity.departParentId}
AND b.in_depart_id = #{entity.departId}
AND date_format(b.audit_date,'%Y-%m') = #{entity.yearMonth}
and b.in_depart_id IN
#{item}
) AS t1 GROUP BY t1.audit_date, t1.depart_id ) AS in_table, ( SELECT sum( t2.product_num ) AS outProductNum, sum( t2.sum_selling_price ) AS outTotalPrice, t2.audit_date, t2.depart_id FROM ( SELECT a.id, a.record_id, b.record_no, b.record_type, b.in_type, b.out_type, a.product_id, a.product_num, a.purchase_price, a.selling_price, a.selling_price * a.product_num AS sum_selling_price, date_format( b.audit_date, '%Y-%m' ) AS audit_date, b.out_depart_id AS depart_id FROM pd_stock_record_detail a LEFT JOIN pd_stock_record b ON a.record_id = b.id AND b.audit_status = '2' WHERE b.del_flag = '0' AND b.record_type = '2' AND ( b.out_type IS NOT NULL AND out_type <> '' )
AND b.depart_parent_id = #{entity.departParentId}
AND b.out_depart_id = #{entity.departId}
AND date_format(b.audit_date,'%Y-%m') = #{entity.yearMonth}
and b.out_depart_id IN
#{item}
) AS t2 GROUP BY t2.audit_date, t2.depart_id ) AS out_table, sys_depart de WHERE in_table.depart_id = out_table.depart_id AND in_table.audit_date = out_table.audit_date AND in_table.depart_id = de.id ORDER BY in_table.audit_date DESC
