mapper.select:
select
  ,
  b.depart_name as inDepartName,
  c.depart_name as outDepartName,
  d.name as supplierName,
  e.realname as submitByName,
  f.realname as auditByName
from
  pd_stock_record a
  left join sys_depart b on a.in_depart_id = b.id
  left join sys_depart c on a.out_depart_id = c.id
  left join pd_supplier d on a.supplier_id = d.id
  left join sys_user e on a.submit_by = e.id
  left join sys_user f on a.audit_by = f.id

      a.del_flag = #{entity.DEL_FLAG_NORMAL}

          AND a.record_no LIKE concat('%',#{entity.recordNo},'%')


          AND a.record_type = #{entity.recordType}


          AND a.out_type = #{entity.outType}


          AND a.out_type = '4'


          AND a.out_type <> '4'


          AND a.in_type = #{entity.inType}


          AND a.audit_status = #{entity.auditStatus}


          AND a.order_no = #{entity.orderNo}


          AND a.merge_order_no = #{entity.mergeOrderNo}


          AND a.allocation_no = #{entity.allocationNo}


          AND a.apply_no = #{entity.applyNo}


          AND a.dosagert_no = #{entity.dosagertNo}


          AND a.submit_by = #{entity.submitBy}


          AND a.submit_status = #{entity.submitStatus}


          AND a.out_depart_id = #{entity.outDepartId}


          AND a.in_depart_id = #{entity.inDepartId}


          AND a.supplier_id = #{entity.supplierId}


          AND a.depart_id = #{entity.departId}


          AND a.depart_parent_id = #{entity.departParentId}


          AND a.submit_date >= #{entity.queryDateStart}


          AND a.submit_date <= #{entity.queryDateEnd}


  order by a.create_time desc

select , b.depart_name as inDepartName, c.depart_name as outDepartName, d.name as supplierName, e.realname as submitByName, f.realname as auditByName from pd_stock_record a left join sys_depart b on a.in_depart_id = b.id left join sys_depart c on a.out_depart_id = c.id left join pd_supplier d on a.supplier_id = d.id left join sys_user e on a.submit_by = e.id left join sys_user f on a.audit_by = f.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.record_no = #{recordNo}

SELECT DISTINCT a.record_no as "recordNo", a.submit_date as "submitDate", c.depart_name as "outDepartName", d.depart_name as "inDepartName", pp.name as "productName", pp.number as "number", pp.spec as "spec", pp.version as "version", srp.product_bar_code as "productBarCode", srp.batch_no as "batchNo", srp.exp_date as "expDate", srp.product_num as "productNum", srp.remarks as "remarks", e.realname as "submitByName" FROM pd_stock_record a LEFT JOIN pd_stock_record_detail srp ON a.id = srp.record_id LEFT JOIN pd_product pp ON srp.product_id = pp.id LEFT JOIN pd_stock_record sr ON a.allocation_no = sr.allocation_no LEFT JOIN sys_depart c ON a.out_depart_id = c.id LEFT JOIN sys_depart d ON a.in_depart_id = d.id LEFT JOIN sys_user e on a.submit_by = e.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.id != sr.id
AND a.record_type = #{recordType}
AND a.in_depart_id = #{inDepartId}
AND a.audit_status = #{auditStatus}
AND a.in_type = #{inType}
AND a.depart_parent_id = #{departParentId}
ORDER BY a.update_time DESC, a.submit_date DESC

select b.id as "productId", b.number as "number", b.name as "productName", b.spec as "spec", case when b.product_flag = '0' then "耗材" when b.product_flag = '1' then "试剂" end as "productFlagName", sum(a.product_num) as "totalSum", sum(a.purchase_price * a.product_num) as "inTotalPrice", c.name as "venderName", d.name as "supplierName", e.name as "unitName" from pd_stock_record_detail a left join pd_stock_record f on a.record_id=f.id and f.del_flag=#{DEL_FLAG_NORMAL} left join pd_product b on a.product_id=b.id left join pd_vender c on b.vender_id=c.id left join pd_supplier d on b.supplier_id=d.id left join pd_unit e on b.unit_id=e.id where a.del_flag=#{DEL_FLAG_NORMAL} and a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) and substr(a.create_time,1,7) <=substr(CURDATE(),1,7)
AND f.depart_parent_id = #{departParentId}
AND f.in_depart_id = #{inDepartId}
AND ( b.number LIKE concat('%',#{productName},'%') or b.name LIKE concat('%',#{productName},'%') or b.py LIKE concat('%',#{productName},'%') or b.wb LIKE concat('%',#{productName},'%') or b.bname LIKE concat('%',#{productName},'%') or b.bpy LIKE concat('%',#{productName},'%') or b.bwb LIKE concat('%',#{productName},'%') or b.zdy LIKE concat('%',#{productName},'%') )
AND b.number LIKE concat('%',#{number},'%')
AND b.supplier_id = #{supplierId}
AND b.spec LIKE concat('%',#{spec},'%')
AND b.product_flag = #{productFlag}
AND b.vender_id = #{venderId}
AND b.registration LIKE concat('%',#{registration},'%')
AND f.record_type = #{recordType}
AND f.audit_status = #{auditStatus}
GROUP BY b.number

select c.type as "type", ROUND(sum(c.y),2) as "金额" from( SELECT DATE_FORMAT(x.Month, '%Y-%m') as "type" , "0" as "y" FROM XNumber x where x.Month > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) and substr(x.Month,1,7) <=substr(CURDATE(),1,7) UNION ALL SELECT DATE_FORMAT(a.create_time, '%Y-%m') AS "type", a.product_num * a.purchase_price AS "y" FROM pd_stock_record_detail a left join pd_stock_record b on a.record_id=b.id and b.del_flag='0' where b.record_type ='1' AND b.audit_status ='2' AND a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) AND substr(a.create_time,1,7) <=substr(CURDATE(),1,7)
and a.product_id=#{productId}
AND b.depart_parent_id = #{departParentId}
AND b.in_depart_id = #{inDepartId}
) c GROUP BY type;

select c.type as "type", ROUND(sum(c.y),2) as "数量" from( SELECT DATE_FORMAT(x.Month, '%Y-%m') as "type" , "0" as "y" FROM XNumber x where x.Month > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) and substr(x.Month,1,7) <=substr(CURDATE(),1,7) UNION ALL SELECT DATE_FORMAT(a.create_time, '%Y-%m') AS "type",a.product_num AS "y" FROM pd_stock_record_detail a left join pd_stock_record b on a.record_id=b.id and b.del_flag='0' where b.record_type ='1' AND b.audit_status ='2' AND a.create_time > DATE_SUB(CURDATE(), INTERVAL 1 YEAR) AND substr(a.create_time,1,7) <=substr(CURDATE(),1,7)
and a.product_id=#{productId}
AND b.depart_parent_id = #{departParentId}
AND b.in_depart_id = #{inDepartId}
) c GROUP BY type;
