mapper.select:
SELECT a.id, a.name, a.unit_type, a.py, a.wb, a.zdy, a.create_by, a.create_time, a.update_by, a.update_time, a.remarks, a.del_flag, a.sys_org_code FROM pd_unit a
a.del_flag = #{DEL_FLAG_NORMAL}
AND ( a.name LIKE concat('%',#{name},'%') or a.py LIKE concat('%',#{name},'%') or a.wb LIKE concat('%',#{name},'%') or a.zdy LIKE concat('%',#{name},'%') )
AND a.unit_type = #{unitType}
AND a.depart_parent_id = #{departParentId}
ORDER BY a.update_time desc,a.create_time desc

SELECT a.id, a.name, a.unit_type, a.py, a.wb, a.zdy, a.create_by, a.create_time, a.update_by, a.update_time, a.remarks, a.del_flag, a.sys_org_code FROM pd_unit a
a.del_flag = #{entity.DEL_FLAG_NORMAL}
AND ( a.name LIKE concat('%',#{entity.name},'%') or a.py LIKE concat('%',#{entity.name},'%') or a.wb LIKE concat('%',#{entity.name},'%') or a.zdy LIKE concat('%',#{entity.name},'%') )
AND a.unit_type = #{entity.unitType}
AND a.depart_parent_id = #{entity.departParentId}
ORDER BY a.update_time desc,a.create_time desc

SELECT
*
FROM pd_unit a

    a.del_flag = #{DEL_FLAG_NORMAL}
    and a.name = #{name}

        AND a.id != #{id}


        AND a.unit_type = #{unitType}


        AND a.depart_parent_id = #{departParentId}
