mapper.select:
SELECT DISTINCT a.id, a.code, a.name, a.sum, a.py, a.wb, a.zdy, a.create_by, a.create_time, a.update_by, a.update_time, a.remarks, a.del_flag, a.sys_org_code, a.depart_id, a.depart_parent_id FROM pd_use_package a left join pd_use_package_detail b on a.id=b.package_id AND b.del_flag='0' left join pd_product c on c.id=b.product_id and c.del_flag='0'
a.del_flag = #{entity.DEL_FLAG_NORMAL}
AND ( a.name LIKE concat('%',#{entity.name},'%') or a.py LIKE concat('%',#{entity.name},'%') or a.wb LIKE concat('%',#{entity.name},'%') or a.zdy LIKE concat('%',#{entity.name},'%') )
AND ( c.name LIKE concat('%',#{productName},'%') or c.py LIKE concat('%',#{productName},'%') or c.wb LIKE concat('%',#{productName},'%') or c.bname LIKE concat('%',#{productName},'%') or c.bpy LIKE concat('%',#{productName},'%') or c.bwb LIKE concat('%',#{productName},'%') or c.zdy LIKE concat('%',#{productName},'%') )
AND c.number LIKE concat('%',#{entity.number},'%')
AND a.code LIKE concat('%',#{entity.code},'%')
AND a.depart_parent_id = #{entity.departParentId}
AND a.depart_id = #{entity.departId}
AND a.id IN
#{item}
ORDER BY a.update_time desc

SELECT
*
FROM pd_use_package a

    a.del_flag = #{DEL_FLAG_NORMAL}
    and a.code = #{code}
    and a.name = #{name}

        AND a.id != #{id}


        AND a.depart_parent_id = #{departParentId}
